mapper.select:
SELECT	*	
FROM		survey
WHERE 	u_idx = #{user.username}
ORDER BY	s_idx DESC
LIMIT		${pagination.pageNum}, ${pagination.perPage}

mapper.insert:
  INSERT INTO item(i_value, s_idx, q_idx) 
  VALUES 




				(#{item.i_value}, #{s_idx}, 
					(	SELECT		q_idx
						FROM		question
						WHERE		s_idx = #{s_idx}
						ORDER BY	q_idx
						LIMIT		1
					) + #{i}
				)
